mapper.select:
SELECT COUNT(order_id)
FROM orders
WHERE order_id = #{orderId}

mapper.update:
UPDATE products
SET sale_quantity = #{resultQuantity}
WHERE id = #{productId}

mapper.select:
SELECT DATE_ADD(DATE_FORMAT(create_date, '%Y-%m-%d'), INTERVAL 14 DAY) as orderCancelPossibleDate
FROM orders
WHERE status = #{status}
and order_id = #{orderId}

SELECT COUNT(order_id)
FROM orders
WHERE order_id = #{orderId}
AND status = #{status}
AND DATE_FORMAT(#{orderCancelPossibleDate}, '%Y-%m-%d') > now()
